nta.declaration:
//Insert declarations of global clocks, variables, constants and channels.

//global consts
const int T = 12;
const int P = 11;
const int PRE = 2;
const int POST = 2;
//numero di CPU
//const int CPU = 0;
const int CPU = 1;
const int TASK = 1;
//const int TT = 0;
//tt guardia sempre vera, come valore di clock x>=0 
const int TT = 1;
const int INF = -1;
const int NONE=-1;

//places
const int agv_carico_A = 0;
const int richiesta_pezzi_B = 1;
const int richiesta_pezzi_A = 2;
const int attesa_B = 3;
const int attesa_A = 4;
const int agv_vuoto_A = 5;
const int agv_vuoto_B = 6;
const int agv_vuoto_I = 7;
const int agv_carico_I = 8;
const int caricamento_agv = 9;
const int agv_carico_B = 10;


//Transitions
const int invio_richiesta_B = 0; //Event
const int invio_richiesta_A = 1; //Event
const int va_in_B = 2; //Event
const int scarica_A = 3; //Event
const int agv_va_in_B = 4; //Event
const int agv_va_in_I = 5; //Event
const int agv_va_in_A = 6; //Event
const int carica_pezzi = 7; //Event
const int nessun_pezzo = 8; //Event
const int rifornimento_pezzi = 9; //Event
const int scarica_B = 10; //Event
const int va_in_I = 11; //Event

//P-Transitions


//TASKs
const int dummy_task=0;

//CPUs


//channels
broadcast chan end_fire;


//clocks
clock t[TASK];
clock x[TASK];


//Useful types
typedef int[0,T-1] tid;
typedef int[0, TASK-1] task_t;
typedef int[0, CPU-1] cpu_t;


//structures
typedef struct{
	int[0,T] index;
	int[0,1] ready;
} R_Info;

typedef struct{
	int[0,T] index;
	int[0,T] prior;
} P_Info;

typedef struct{
  int index;
  int weight;
} Info;


//FPS data:
P_Info fixed_priority[CPU][TASK][TT];

//EDF data:
const int deadline[TASK]={0};

//Requests data:
R_Info request[CPU][TASK][TT];


//Backward incidence matrix:
const Info B[T][PRE] = {
{{attesa_A,1},{richiesta_pezzi_B,0}}, // invio_richiesta_B
{{attesa_B,1},{richiesta_pezzi_A,0}}, // invio_richiesta_A
{{agv_carico_A,1},{richiesta_pezzi_A,0}}, // va_in_B
{{agv_carico_A,1},{richiesta_pezzi_A,1}}, // scarica_A
{{agv_vuoto_A,1},{NONE,0}}, // agv_va_in_B
{{agv_vuoto_B,1},{NONE,0}}, // agv_va_in_I
{{agv_carico_I,1},{NONE,0}}, // agv_va_in_A
{{agv_vuoto_I,1},{caricamento_agv,1}}, // carica_pezzi
{{agv_vuoto_I,1},{caricamento_agv,0}}, // nessun_pezzo
{{caricamento_agv,0},{NONE,0}}, // rifornimento_pezzi
{{agv_carico_B,1},{richiesta_pezzi_B,1}}, // scarica_B
{{agv_carico_B,1},{richiesta_pezzi_B,0}} // va_in_I
};


//Forward incidence matrix:
const Info F[T][POST]= {
{{richiesta_pezzi_B,1},{NONE,0}}, // invio_richiesta_B
{{richiesta_pezzi_A,1},{NONE,0}}, // invio_richiesta_A
{{agv_carico_B,1},{NONE,0}}, // va_in_B
{{agv_vuoto_A,1},{attesa_A,1}}, // scarica_A
{{agv_vuoto_B,1},{NONE,0}}, // agv_va_in_B
{{agv_vuoto_I,1},{NONE,0}}, // agv_va_in_I
{{agv_carico_A,1},{NONE,0}}, // agv_va_in_A
{{agv_carico_I,1},{NONE,0}}, // carica_pezzi
{{agv_vuoto_I,1},{NONE,0}}, // nessun_pezzo
{{caricamento_agv,1},{NONE,0}}, // rifornimento_pezzi
{{agv_vuoto_B,1},{attesa_B,1}}, // scarica_B
{{agv_vuoto_I,1},{NONE,0}} // va_in_I
};


//Fire window vector:
const int I[T][2] = {
{0,1}, // invio_richiesta_B
{0,1}, // invio_richiesta_A
{0,1}, // va_in_B
{0,1}, // scarica_A
{0,1}, // agv_va_in_B
{0,1}, // agv_va_in_I
{0,1}, // agv_va_in_A
{0,1}, // carica_pezzi
{0,1}, // nessun_pezzo
{0,1}, // rifornimento_pezzi
{0,1}, // scarica_B
{0,1} // va_in_I
};


//Mark vector:
int[0,1] M[P]={ 0,0,0,1,1,0,0,1,0,0,0 };


//NEXT ON CPU
//initial value to zero
int[NONE, T-1] nxt[CPU]={0};
int[NONE, T-1] busy[CPU]={0};

//GLOBAL FUNCTIONS
void reset(){
   int c;
   for( c=0; c<CPU; ++c ) nxt[c]=NONE;
}//reset

bool enabled( const tid ID ){
  int p;
  for( p=0; p<PRE && B[ID][p].index!=-1 ; ++p )
    if( B[ID][p].weight>0 && M[B[ID][p].index]<B[ID][p].weight ||
        B[ID][p].weight==0 && M[B[ID][p].index]>0 )
      return false;
  return true;
}//enabled

void withdraw( const tid ID ){
  int p;
  for( p=0; p<PRE && B[ID][p].index!=-1; ++p ){
    M[B[ID][p].index]-=B[ID][p].weight;
  }
}//withdraw

void deposit( const tid ID ){
  int p;
  for( p=0; p<POST && F[ID][p].index!=-1; ++p ){
   //check for M[4] out of range
    if(M[F[ID][p].index]<1)
        M[F[ID][p].index]+=F[ID][p].weight;
  }
  reset();
}//deposit

bool quiet(){
  return forall( c:cpu_t ) nxt[c]==NONE;
}//quiet

template.declaration:
//Insert local declarations of clocks, variables and constants.

bool this; //this task instance

void arrival(){
   int[0,TT] j;
   for( j=0; j<TT; j++ )
       if( request[cpu][p][j].index==ID ){
             request[cpu][p][j].ready++; return;
       }
}//arrival

void finish(){
   int[0,TT] j;
   for( j=0; j<TT; j++ )
       if( request[cpu][p][j].index==ID ){
            request[cpu][p][j].ready--;  
            return;
       }
}//finish


bool requests(){
   int i,j;
   for( i=0; i<TASK; i++ )
      for( j=0; j<TT; j++ )
          if( request[cpu][i][j].index!=T && request[cpu][i][j].ready>0 ) return true;
   return false;
}//requests

int[0,T] next(){
   int[0,TASK] i;
   int[0,TT] j, hpid;
   int[-1,TASK] hpt=-1;

   for( i=0; i<TASK; i++ )
      for( j=0; j<TT; j++ )
         if( request[cpu][i][j].index<T && request[cpu][i][j].ready>0 ){
             if( hpt==NONE || fixed_priority[cpu][i][j].prior>fixed_priority[cpu][hpt][hpid].prior ){
                 hpt=i; hpid=j;
             }
         }

   return fixed_priority[cpu][hpt][hpid].index;
}//next


int[0,TASK] i;
int[0,TT] j;
tid hpid;
int[-1,TASK-1] hpt=-1;

bool requests(){
   int i,j;
   for( i=0; i<TASK; i++ )
      for( j=0; j<TT; j++ )
          if( request[cpu][i][j].index!=T && 
              request[cpu][i][j].ready>0 ) return true;
   return false;
}//requests


bool requests(){
   int i,j;
   for( i=0; i<TASK; i++ )
      for( j=0; j<TT; j++ )
          if( request[cpu][i][j].index!=T && request[cpu][i][j].ready>0 ) return true;
   return false;
}//requests

int[0,T] next(){
   int[0,TASK] i;
   int[0,TT] j, hpid;
   int[-1,TASK] hpt=-1;

   if(busy[cpu]!=NONE)
        return busy[cpu];

   for( i=0; i<TASK; i++ )
      for( j=0; j<TT; j++ )
         if(request[cpu][i][j].index<T && request[cpu][i][j].ready>0 ){
             if( hpt==-1 || fixed_priority[cpu][i][j].prior>fixed_priority[cpu][hpt][hpid].prior ){
                 hpt=i; hpid=j;
             }
         }

   return fixed_priority[cpu][hpt][hpid].index;
}//next


int[0,TASK] i;
int[0,TT] j;
tid hpid;
int[-1,TASK-1] hpt=-1;

bool requests(){
   int i,j;
   for( i=0; i<TASK; i++ )
      for( j=0; j<TT; j++ )
          if( request[cpu][i][j].index!=T && 
              request[cpu][i][j].ready>0 ) return true;
   return false;
}//requests

nta.system:
//Insert process assignements.
//Transitions e_invio_richiesta_B = Transition(invio_richiesta_B, dummy_task); //Event transition e_invio_richiesta_A = Transition(invio_richiesta_A, dummy_task); //Event transition e_va_in_B = Transition(va_in_B, dummy_task); //Event transition e_scarica_A = Transition(scarica_A, dummy_task); //Event transition e_agv_va_in_B = Transition(agv_va_in_B, dummy_task); //Event transition e_agv_va_in_I = Transition(agv_va_in_I, dummy_task); //Event transition e_agv_va_in_A = Transition(agv_va_in_A, dummy_task); //Event transition e_carica_pezzi = Transition(carica_pezzi, dummy_task); //Event transition e_nessun_pezzo = Transition(nessun_pezzo, dummy_task); //Event transition e_rifornimento_pezzi = Transition(rifornimento_pezzi, dummy_task); //Event transition e_scarica_B = Transition(scarica_B, dummy_task); //Event transition e_va_in_I = Transition(va_in_I, dummy_task); //Event transition
//Scheduler (FPS, EDF, NP_FPS or NP_EDF) declaration
//System: system Starter, e_invio_richiesta_B, e_invio_richiesta_A, e_va_in_B, e_scarica_A, e_agv_va_in_B, e_agv_va_in_I, e_agv_va_in_A, e_carica_pezzi, e_nessun_pezzo, e_rifornimento_pezzi, e_scarica_B, e_va_in_I;
